    const[state, setState] = useState(1);
    const pod = useCustomProduct(state);
    // const [pod, params]...

    console.log(pod);

useEffect(() => {
    const parent = document.getElementById("parentId");
    parent?.addEventListener("scroll",onScroll);
},[]);

