import { log } from "console";
import { useCustomProduct } from "./Hooks";
import { useEffect, useRef, useState } from 'react';
import TextField from "@mui/material/TextField";
import "./Home.scss";
import { FormControl, FormControlLabel, FormLabel, Radio, RadioGroup } from "@mui/material";


const Home = () =>{
    const[loadMore, _setLoadMore] = useState(true);
    const pod = useCustomProduct(loadMore);
    // const [pod, params]...

    //console.log(pod);
    const noviRef = useRef<number | any>(0);
    const loadMoreRef = useRef(loadMore);
    const setLoadMore = (e: any) => {
        loadMoreRef.current = e;
        _setLoadMore(e);
    }
console.log(pod);

    useEffect(() => {
        if(loadMore && pod.length > 0) {
            setTimeout(() => {
                setUserList(pod);
                setLoadMore(false);
            }, 2000);
        }
        return () => {
                clearTimeout();
        }
    },[pod]);

    useEffect(() => {
        const parent = document.getElementById("parentId");
        parent?.addEventListener("scroll",onScroll);
        return () => parent?.removeEventListener("scroll",onScroll);
    },[]);

    console.log(loadMore);



    const onScroll = (e: any) => {
    
        console.log("uslo je u f-ju");
        
        console.log(e.target.offsetHeight);
        console.log(e.target.scrollTop);
        console.log(e.target.scrollHeight);

        if(loadMoreRef.current)
            console.log("finish");
        else if (e.target.offsetHeight + e.target.scrollTop >= e.target.scrollHeight){
            debugger;
            console.log("uslov");
            if(!loadMoreRef.current)
                setLoadMore(true);
            noviRef.current = e.target.scrollHeight;
        }
    
    }

    const [text,setText] = useState('');
    const [status,setStatus] = useState('Any');
    const [userList,setUserList] = useState<{ created:string,episode: Array<string>,gender:string,id:number,image:string,location:Array<string>,name:string,origin:Array<string>,species:string,status:string,type:string,url:string}[] | undefined>(pod);

    const handleOnClick = (e: any) =>{
        const findUser = pod && pod?.length > 0 ? pod?.filter(u => u.name.toLocaleLowerCase().includes(e)):undefined;
        console.log(text);
        console.log(userList);
        setText(e);
        setUserList(findUser);
    }
    console.log(userList);

    const handleChange = (e: any) =>{
        console.log(e);
        if(e === "Any")
            setUserList(pod);
        else {    
            const findUser = pod && pod?.length > 0 ? pod?.filter(u => u.status.includes(e)):undefined;
            console.log(status);
            console.log(userList);
            setUserList(findUser);
        }
        setStatus(e);
    }
    
    return(
        <div id="parentId" style={{ height: "calc(100vh - 16px)", overflow: "auto" }}>
            {/*pod instanceof Array && pod.map(x => x.name)*/}
            <div className="searchDiv">
            <TextField
                className="search" 
                id="outlined-basic" 
                label="search" 
                variant="outlined" 
                type="text"
                placeholder="search"
                value={text}
                onChange={(e)=>{handleOnClick(e.target.value)}}
            />
            </div>
            <FormControl component="fieldset">
                <FormLabel component="legend">Gender</FormLabel>
                <RadioGroup style={{ display: "flex", flexDirection: "row" }} aria-label="gender" name="gender1" value={status} onChange={(e) =>{handleChange(e.target.value)}}>
                    <FormControlLabel value="Any" control={<Radio />} label="Any" />
                    <FormControlLabel value="Alive" control={<Radio />} label="Alive" />
                    <FormControlLabel value="Dead" control={<Radio />} label="Dead" />
                    <FormControlLabel value="unknown" control={<Radio />} label="Unknown" />
                </RadioGroup>
            </FormControl>
            

            <div className="okvir">
            {/*pod instanceof Array && pod.map(x => {
                return(
                    
                    <div id="jedan"key={x.id}>
                        <img src={x.image}/>
                        <p>{x.name}</p>
                    </div>
                    
                )
            })*/}
            {
                userList && userList?.length > 0 && 
                    userList?.map((user)=>{
                        return(
                            <div className="cart" key={user.id}>
                                <img className="image" src={user.image}/>
                                <p className="name">{user.name}</p>
                                <p>{/*user.status*/}</p>
                             </div>
                        )
                    })
            }
            </div>
            {loadMoreRef.current && <div style={{ height: "120px" }}>Loading...</div>}
        </div>
    )
}

export default Home;
