import axios from "axios";
import { useEffect, useState } from "react";

export interface IRes{
    created:string,
    episode: Array<string>
    gender:string,
    id:number,
    image:string,
    location:Array<string>,
    name:string,
    origin:Array<string>,
    species:string,
    status:string,
    type:string,
    url:string
}

export interface IInf{
    count:number,
    next:string,
    pages:number,
    prev:null
}


export const useCustomProduct = (state: number) =>{
    const [pod,setPod] = useState<IRes[]>([]);
    const [params,setParams] = useState({ count: 0, next: "https://rickandmortyapi.com/api/character", pages: 0, prev: "" })

    useEffect(()=>{
        getData();
    },[state]);

    const getData = () => {
        axios.get(params.next)
        .then( response => {//console.log(response.data)
            setPod(response.data.results);
            setParams(response.data.info);       
        });
    }

    return pod;
    // return [pod, params];

}
