import axios from "axios";
import { useEffect, useState } from "react";

export interface IRes{
    created:string,
    episode: Array<string>
    gender:string,
    id:number,
    image:string,
    location:Array<string>,
    name:string,
    origin:Array<string>,
    species:string,
    status:string,
    type:string,
    url:string
}

export interface IInf{
    count:number,
    next:string,
    pages:number,
    prev:null
}


export const useCustomProduct = () =>{
    const [pod,setPod] = useState<IRes[]>([]);
    const [params,setParams] = useState({ count: 0, next: "https://rickandmortyapi.com/api/character", pages: 0, prev: "" })

    useEffect(()=>{
        axios.get(params.next)
        .then( response => {
            setPod(response.data.result);
            setParams(response.data.info.pages);       
        });

    },[]);

    const getData = () => {
        axios.get(params.next)
        .then( response => {console.log(response.data)

            params.page++;
            setPod(response.data.result);
            setParams(response.data.info.pages);       
        });
    }

    return [pod, getData, params];

}
