import { log } from "console";
import { useCustomProduct } from "./Hooks";
import { useEffect, useState } from 'react';

const Home = () =>{
    const[state, setState] = useState(1);
    const pod = useCustomProduct(state);
    // const [pod, params]...

    //console.log(pod);




    useEffect(() => {

        
        const parent = document.getElementById("parentId");
        parent?.addEventListener("scroll",onScroll);
        

    },[]);



    const onScroll = (e: Event) => {
    
        //doslo do kraja -> setState(state+1);

        /*if ((window.innerHeight + Math.round(window.scrollY)) >= document.body.offsetHeight) {
            setState(state+1);
        }*/
        console.log("udji ovde");
        
        if (document.body.offsetHeight + document.body.scrollTop>= document.body.scrollHeight){
            setState(state+1)
        }


    
    }


    
    return(
        <div id="parentId">
            {/*pod instanceof Array && pod.map(x => x.name)*/}


            {pod instanceof Array && pod.map(x => {
                return(
                    
                    <div key={x.id}>
                        <img src={x.image}/>
                        <p>{x.name}</p>
                    </div>
                    
                )
            })}
            
        </div>
    )
}

export default Home;
