import { log } from "console";
import { useCustomProduct } from "./Hooks";
import { useEffect, useRef, useState } from 'react';
import TextField from "@mui/material/TextField";
import "./Home.scss";


const Home = () =>{
    const[state, _setState] = useState(1);
    const pod = useCustomProduct(state);
    // const [pod, params]...

    //console.log(pod);

    const stateRef = useRef(state);
    const setState = (e: any) => {
        stateRef.current = e;
        _setState(e);
    }



    useEffect(() => {

        window.addEventListener('scroll', function(e) {
  console.log(e);
});

        const parent = document.getElementById("parentId");
        parent?.addEventListener("scroll",onScroll);

        /*const parent: HTMLElement | null = document.getElementById("parentId");
        console.log();
        

        function onScroll(event: Event) {
            console.log("pr");
            
            setState(state+1);
          }
          
          if (parent) {
            parent.addEventListener("scroll", onScroll);
            console.log("jos");
            
        }*/
        

    },[]);

console.log(state);



    const onScroll = (e: any) => {
    
        //doslo do kraja -> setState(state+1);

        /*if ((window.innerHeight + Math.round(window.scrollY)) >= document.body.offsetHeight) {
            setState(state+1);
        }*/
        console.log(e);
        
        if (e.target.offsetHeight + e.target.scrollTop>= e.target.scrollHeight){
            setState(stateRef.current + 1)
        }


    
    }

    const [text,setText] = useState('');
    const [userList,setUserList] = useState<{ created:string,episode: Array<string>,gender:string,id:number,image:string,location:Array<string>,name:string,origin:Array<string>,species:string,status:string,type:string,url:string}[] | undefined>(pod);

    const handleOnClick = () =>{
        const findUser =userList && userList?.length > 0 ? userList?.filter(u => u?.name === text):undefined;
        setUserList(findUser);

    }

    
    return(
        <div id="parentId" style={{ height: "100vh" }}>
            {/*pod instanceof Array && pod.map(x => x.name)*/}
            <TextField
                className="search" 
                id="outlined-basic" 
                label="search" 
                variant="outlined" 
                type="text"
                placeholder="search"
                value={text}
                onChange={(e)=>{setText(e.target.value)}}
                
            />
            <button disabled={!text} onClick={handleOnClick}>Dugme</button>
            

            <div className="okvir">
            {pod instanceof Array && pod.map(x => {
                return(
                    
                    <div id="jedan"key={x.id}>
                        <img src={x.image}/>
                        <p>{x.name}</p>
                    </div>
                    
                )
            })}
            {/*
                userList && userList?.length > 0 && 
                    userList?.map((user)=>{
                        return(
                            <div>
                                {user.name}
                            </div>
                        )
                    })
            */}
            </div>
            
        </div>
    )
}

export default Home;
