import axios from "axios";
import { useEffect, useState } from "react";

export interface IRes{
    created:string,
    episode: Array<string>
    gender:string,
    id:number,
    image:string,
    location:Array<string>,
    name:string,
    origin:Array<string>,
    species:string,
    status:string,
    type:string,
    url:string
}

export interface IInf{
    count:number,
    next:string,
    pages:number,
    prev:null
}


export const useCustomProduct = () =>{
    const [pod,setPod] = useState<IRes[]>([]);
    const [params,setParams] = useState({page:1})

    useEffect(()=>{
        axios.get("https://rickandmortyapi.com/api/character", {params: {page: params.page}})
        .then( response => {console.log(response.data)

            params.page++;
            setPod(response.data.result);
            setParams(response.data.info.pages);       
        });

    },[]);

    const getData = () => {
        axios.get("https://rickandmortyapi.com/api/character", {params: {page: params.page}})
        .then( response => {console.log(response.data)

            params.page++;
            setPod(response.data.result);
            setParams(response.data.info.pages);       
        });
    }

    return [pod, getData, params];

}
